-- VIEWS ESTATÍSTICAS CAIXA

-- total de vendas (quantidade)
DROP VIEW IF EXISTS `pdv_tot_vendas`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW  `pdv_tot_vendas` AS select 'mdi-basket' AS `Icon`,'teal darken-2' AS `ColorIcon`,'Vendas' AS `Header`,count(0) AS `Value`,time_format(now(),'%H:%i') AS `LastUpdate` from (`vd1` join `cv5` on(((`cv5`.`CV5PEDIDO` = `vd1`.`VD1PEDIDO`) and (`cv5`.`CV5SITUA` = '') and (`cv5`.`CV5EMITIDO` = 1) and (`cv5`.`CV5ENTSAI` = 1) and (`vd1`.`VD1CONSIG` = 0))))INNER JOIN VD6 ON VD6PEDIDO = VD1PEDIDO where (date_format(`vd1`.`VD1ENTRADA`,'%Y-%m-%d') = cast(now() as date)) AND (CV5MODELO <> '55' OR (CV5MODELO = '55' AND NOT EXISTS (SELECT CV5DOC FROM CV5 WHERE CV5PEDIDO = VD1PEDIDO AND CV5EMBARQ = VD6EMBARQ AND CV5MODELO = '99'  AND CV5SITUA = '' AND CV5EMITIDO = 1 AND CV5ENTSAI = 1)));

-- total de itens vendidos (quantidade)
DROP VIEW IF EXISTS `pdv_tot_qtde_vendas`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW  `pdv_tot_qtde_vendas` AS select 'mdi-package' AS `Icon`, 'brown' AS `ColorIcon`, 'Produtos vendidos' AS `Header`, sum(`vd3`.`VD3QTDE`) AS `VALUE`, time_format(now(),'%H:%i') AS `LastUpdate` from ((`vd1` join `vd3` on((`vd3`.`VD3PEDIDO` = `vd1`.`VD1PEDIDO`))) INNER JOIN VD6 ON VD6PEDIDO = VD1PEDIDO join `cv5` on(((`cv5`.`CV5PEDIDO` = `vd1`.`VD1PEDIDO`) and (`cv5`.`CV5SITUA` = '') and (`cv5`.`CV5EMITIDO` = 1) and (`cv5`.`CV5ENTSAI` = 1) and (`vd1`.`VD1CONSIG` = 0)))) where (date_format(`vd1`.`VD1ENTRADA`,'%Y-%m-%d') = cast(now() as date)) AND (CV5MODELO <> '55' OR (CV5MODELO = '55' AND NOT EXISTS (SELECT CV5DOC FROM CV5 WHERE CV5PEDIDO = VD1PEDIDO AND CV5EMBARQ = VD6EMBARQ AND CV5MODELO = '99'  AND CV5SITUA = '' AND CV5EMITIDO = 1 AND CV5ENTSAI = 1))) order by value ;

-- vendas para novos cliente no dia (soma)
DROP VIEW IF EXISTS `pdv_qtde_vendas_novos_clientes`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW  `pdv_qtde_vendas_novos_clientes` AS  select 'mdi-account-cash' AS `Icon`,'light-blue darken-3' AS `ColorIcon`,'Vendas - Novos clientes' AS `Header`,count(`vd1`.`VD1PEDIDO`) AS `VALUE`,time_format(now(),'%H:%i') AS `LastUpdate` from (`vd1` join `cf1` on((`cf1`.`CF1CLIENTE` = `vd1`.`VD1CLIENTE`))) INNER JOIN VD6 ON VD6PEDIDO = VD1PEDIDO where ((`vd1`.`VD1CONSIG` = 0) and (date_format(`cf1`.`CF1ABERT`,'%Y-%m-%d') = cast(now() as date)) and `vd1`.`VD1PEDIDO` in (select `cv5`.`CV5PEDIDO` from `cv5` where ((`cv5`.`CV5SITUA` = '') and (`cv5`.`CV5EMITIDO` = 1) and (`cv5`.`CV5ENTSAI` = 1) AND (CV5MODELO <> '55' OR (CV5MODELO = '55' AND NOT EXISTS (SELECT CV5DOC FROM CV5 WHERE CV5PEDIDO = VD1PEDIDO AND CV5EMBARQ = VD6EMBARQ AND CV5MODELO = '99'  AND CV5SITUA = '' AND CV5EMITIDO = 1 AND CV5ENTSAI = 1))))));

-- Total de vendas (soma)
DROP VIEW IF EXISTS `pdv_valor_vendas`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW  `pdv_valor_vendas` AS SELECT 'mdi-currency-usd' AS Icon, 'green' AS ColorIcon,'Total de vendas' AS Header,
SUM(CV5TOTALNF) AS Value,TIME_FORMAT(NOW(), '%H:%i') AS LastUpdate  FROM (VD1 INNER JOIN CV5 ON CV5PEDIDO = VD1PEDIDO AND CV5SITUA = '' AND CV5EMITIDO = 1 AND CV5ENTSAI=1 AND VD1CONSIG = 0)
INNER JOIN VD6 ON VD6PEDIDO = VD1PEDIDO   WHERE (date_format(`cv5`.`cv5emissao`,'%Y-%m-%d') = cast(now() as date))
AND (CV5MODELO <> '55' OR (CV5MODELO = '55' AND NOT EXISTS (SELECT CV5DOC FROM CV5 WHERE CV5PEDIDO = VD1PEDIDO AND CV5EMBARQ = VD6EMBARQ AND CV5MODELO = '99'  AND CV5SITUA = '' AND CV5EMITIDO = 1 AND CV5ENTSAI = 1)));

