--ALTER572

-- Total de vendas (soma)
DROP VIEW IF EXISTS `pdv_valor_vendas`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW  `pdv_valor_vendas` AS SELECT 'mdi-currency-usd' AS Icon, 'green' AS ColorIcon,'Total de vendas' AS Header,
SUM(CV8VALOR) AS Value,TIME_FORMAT(NOW(), '%H:%i') AS LastUpdate
FROM (VD1 INNER JOIN CV5 ON CV5PEDIDO = VD1PEDIDO AND CV5SITUA = '' AND CV5EMITIDO = 1 AND CV5ENTSAI=1 AND VD1CONSIG = 0 INNER JOIN LX0 ON LX0CLIENTE = CV5EMISSOR INNER JOIN CV8 ON CV8DOC = CV5DOC AND CV8TIPO = CV5TIPO AND CV8EMISSOR = CV5EMISSOR)
INNER JOIN VD6 ON VD6PEDIDO = VD1PEDIDO
WHERE date_format(cv5emissao,'%Y-%m-%d') = cast(now() as date)
AND ((CV5MODELO <> '55' AND CV5MODELO <> '65') OR ((CV5MODELO = '55' OR CV5MODELO = '65') AND NOT EXISTS (SELECT CV5.CV5DOC FROM CV5 WHERE  CV5.CV5MODELO = '99' AND CV5PEDIDO=VD1PEDIDO AND CV5EMBARQ=VD6EMBARQ AND CV5.CV5SITUA = '' AND CV5.CV5EMITIDO = 1 AND CV5.CV5ENTSAI = 1)));

-- total de vendas (quantidade)
DROP VIEW IF EXISTS `pdv_tot_vendas`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW  `pdv_tot_vendas` AS SELECT 'mdi-basket' AS Icon,'teal darken-2' AS ColorIcon,'Vendas' AS Header, count(0) AS Value, time_format(now(),'%H:%i') AS LastUpdate
FROM (VD1 JOIN CV5 ON CV5PEDIDO = VD1PEDIDO AND CV5SITUA = '' AND CV5EMITIDO = 1 AND CV5ENTSAI = 1 AND VD1CONSIG = 0 INNER JOIN LX0 ON LX0CLIENTE = CV5EMISSOR)
INNER JOIN VD6 ON VD6PEDIDO = VD1PEDIDO
WHERE DATE_FORMAT(VD1ENTRADA,'%Y-%m-%d') = CAST(NOW() AS DATE)
AND ((CV5MODELO <> '55' AND CV5MODELO <> '65') OR ((CV5MODELO = '55' OR CV5MODELO = '65') AND NOT EXISTS (SELECT CV5.CV5DOC FROM CV5 WHERE  CV5.CV5MODELO = '99' AND CV5PEDIDO=VD1PEDIDO AND CV5EMBARQ=VD6EMBARQ AND CV5.CV5SITUA = '' AND CV5.CV5EMITIDO = 1 AND CV5.CV5ENTSAI = 1)));

-- total de itens vendidos (quantidade)
DROP VIEW IF EXISTS `pdv_tot_qtde_vendas`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW  `pdv_tot_qtde_vendas` AS SELECT 'mdi-package' AS Icon, 'brown' AS ColorIcon, 'Produtos vendidos' AS Header, ROUND(SUM(CV7QTDE),0) AS VALUE, TIME_FORMAT(NOW(),'%H:%i') AS LastUpdate
FROM (VD1 INNER JOIN VD6 ON VD6PEDIDO = VD1PEDIDO INNER JOIN CV5 ON CV5PEDIDO = VD1PEDIDO AND CV5EMBARQ = VD6EMBARQ AND CV5SITUA = '' AND CV5EMITIDO = 1 AND CV5ENTSAI = 1 AND VD1CONSIG = 0 INNER JOIN LX0 ON LX0CLIENTE = CV5EMISSOR INNER JOIN CV7 ON CV7DOC = CV5DOC AND CV7TIPO = CV5TIPO AND CV7EMISSOR = CV5EMISSOR)
WHERE DATE_FORMAT(VD1ENTRADA,'%Y-%m-%d') = CAST(NOW() AS DATE)
AND ((CV5MODELO <> '55' AND CV5MODELO <> '65') OR ((CV5MODELO = '55' OR CV5MODELO = '65') AND NOT EXISTS (SELECT CV5.CV5DOC FROM CV5 WHERE  CV5.CV5MODELO = '99' AND CV5PEDIDO=VD1PEDIDO AND CV5EMBARQ=VD6EMBARQ AND CV5.CV5SITUA = '' AND CV5.CV5EMITIDO = 1 AND CV5.CV5ENTSAI = 1)));

-- vendas para novos cliente no dia (soma)
DROP VIEW IF EXISTS `pdv_qtde_vendas_novos_clientes`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW  `pdv_qtde_vendas_novos_clientes` AS SELECT 'mdi-account-cash' AS Icon,'light-blue darken-3' AS ColorIcon,'Vendas - Novos clientes' AS Header,count(VD1PEDIDO) AS VALUE, TIME_FORMAT(NOW(),'%H:%i') AS LastUpdate
FROM (VD1 INNER JOIN CF1 ON CF1CLIENTE = VD1CLIENTE INNER JOIN VD6 ON VD6PEDIDO = VD1PEDIDO)
WHERE VD1CONSIG = 0
AND DATE_FORMAT(CF1ABERT,'%Y-%m-%d') = CAST(NOW() AS DATE)
AND VD1PEDIDO IN (SELECT CV5PEDIDO FROM CV5 WHERE CV5SITUA = '' AND CV5EMITIDO = 1 AND CV5ENTSAI = 1 AND ((CV5MODELO <> '55' AND CV5MODELO <> '65') OR ((CV5MODELO = '55' OR CV5MODELO = '65') AND NOT EXISTS (SELECT CV5.CV5DOC FROM CV5 WHERE  CV5.CV5MODELO = '99' AND CV5PEDIDO=VD1PEDIDO AND CV5EMBARQ=VD6EMBARQ AND CV5.CV5SITUA = '' AND CV5.CV5EMITIDO = 1 AND CV5.CV5ENTSAI = 1))));

